import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[appCreditCard]',
})
export class CreditCardDirective {
  constructor(private element: ElementRef) {
    console.log('===> Element: ', this.element);
  }
}

import { Directive, HostBinding, HostListener } from '@angular/core';

@Directive({
  selector: '[appCreditCard]',
})
export class CreditCardDirective {
  @HostBinding('style.border') border = '';

  @HostListener('input', ['$event']) onKeyDown(event: KeyboardEvent): void {
    const input = event.target as HTMLInputElement;

    let formattedValue = this.trimNumber(input.value);

    this.border = '';
    if (/[^\d]+/.test(formattedValue)) {
      this.border = '1px solid red';
    }

    formattedValue = this.formatNumber(formattedValue);
    input.value = formattedValue;
  }

  private trimNumber(value: string): string {
    let trimmed = value.replace(/\s+/g, '');

    if (trimmed.length > 16) {
      trimmed = trimmed.substr(0, 16);
    }

    return trimmed;
  }

  private formatNumber(value: string): string {
    const numbers: string[] = [];
    for (let i = 0; i < value.length; i += 4) {
      numbers.push(value.substr(i, 4));
    }

    return numbers.join(' ');
  }
}

import { Directive, HostListener } from '@angular/core';

@Directive({
  selector: '[appCreditCard]',
})
export class CreditCardDirective {
  @HostListener('input', ['$event']) onKeyDown(event: KeyboardEvent): void {
    const input = event.target as HTMLInputElement;

    let trimmed = input.value.replace(/\s+/g, '');
    if (trimmed.length > 16) {
      trimmed = trimmed.substr(0, 16);
    }

    //  Fix Me - Strict types and lint should catch this implicit any...
    const numbers = [];

    for (let i = 0; i < trimmed.length; i += 4) {
      numbers.push(trimmed.substr(i, 4));
    }

    input.value = numbers.join(' ');
  }
}

import { Directive, Input, TemplateRef, ViewContainerRef } from '@angular/core';

@Directive({
  selector: '[appMyFor][appMyForOf]]',
})
export class MyForDirective {
  @Input() set appMyForOf(collection: unknown[]) {
    this.view.clear(); //  The real ngFor would clear only the nodes that changed

    collection.forEach((item, index) => {
      this.view.createEmbeddedView(this.template, {
        $implicit: item,
        index, //  shorthand for index: index
      });
    });
  }

  constructor(
    private view: ViewContainerRef,
    private template: TemplateRef<unknown>
  ) {}
}

