import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { Observable } from 'rxjs';
import { map, startWith, tap } from 'rxjs/operators';
import { Product } from '../../../shared/interfaces/product';
import {
  StockFormManager,
  StockFormValue,
} from '../../models/stock-form-manager';

@Component({
  selector: 'app-stock-selector',
  templateUrl: './stock-selector.component.html',
  styleUrls: ['./stock-selector.component.scss'],
})
export class StockSelectorComponent implements OnInit {
  @Input() set formManager(formManager: StockFormManager) {
    this._formManager = formManager;
  }
  get formManager(): StockFormManager {
    if (!this._formManager) {
      throw new Error('Missing Form Manager!');
    }

    return this._formManager;
  }
  private _formManager: StockFormManager | null = null;

  @Input() products: Product[] = [];

  @Output() add = new EventEmitter<StockFormValue>();

  get parent(): FormGroup {
    return this.formManager.form;
  }

  addDisabled$ = new Observable<boolean>().pipe(startWith(true));

  ngOnInit(): void {
    this.initValueChanges();
  }

  initValueChanges(): void {
    this.addDisabled$ = this.formManager
      .getStockSelectorProductIdValueChanges()
      .pipe(
        tap((value) => console.log('ProductId Value Changes: ', value)),
        startWith(''),
        map((value) => {
          return !value;
        })
      );
  }

  onAdd(): void {
    const selectedStockItem = this.formManager.getSelectedStockFormGroupValue();
    console.log('Selector value', selectedStockItem);

    this.add.emit(selectedStockItem);

    this.formManager.resetStockSelectorFormGroup();
  }
}

styleUrlsData.data:
.stock-selector {
  padding: 0 0 20px;
  margin: 0 0 20px;
  border-bottom: 1px solid #ccc;
  position: relative;

  &__error {
    position: absolute;
    background: #b52d30;
    color: #fff;
    font-weight: 500;
    font-size: 12px;
    text-transform: uppercase;
    border-radius: 3px;
    left: 0;
    bottom: -10px;
    line-height: 1;
    padding: 6px 10px;

    &:before {
      width: 0;
      height: 0;
      border-style: solid;
      border-width: 0 5px 5px 5px;
      border-color: transparent transparent #b52d30 transparent;
      content: " ";
      display: block;
      position: absolute;
      top: -5px;
      left: 10px;
    }
  }

  & > div {
    display: flex;
    align-items: center;
    justify-content: space-between;

    select {
      flex: 1 0;
    }
    button {
      flex: 0 0 100px;
      margin-left: 30px;
    }
    stock-counter {
      flex: 0 0 50px;
      margin-left: 30px;
    }
  }
}

